const t=()=>{let t=$(".glow.text");t.each(()=>{let e=t.offset().top-50;$(window).scrollTop()>=e-$(window).height()?t.addClass("shine"):t.removeClass("shine")})};$(window).scroll(()=>{t()}),$(window).on("load",()=>{let e=$(".glow.text");e.each(()=>{let t=e.text(),l="";t.split("").forEach((t,e)=>{l+=`<span style="animation-delay:${e/10}s;">${t}</span>`}),e.html(l)}),t()});