const n=()=>{let n=$(".glow.text");n.each(()=>{let o=n.offset().top-50;let t=$(window).scrollTop();let e=$(window).height();t>=o-e?n.addClass("shine"):n.removeClass("shine")})};$(window).scroll(()=>{n()}),$(window).on("load",()=>{let o=$(".glow.text");o.each(()=>{let n=o.text();let t="";n.split("").forEach((n,o)=>{let e=o/10;t+=`<span style="animation-delay:${e}s;">${n}</span>`});o.html(t)});n()});